function loadEquipments() {
    let categoryId = document.getElementById('category').value;
    let equipmentSelect = document.getElementById('equipment');
    let surveySelect = document.getElementById('survey');

    if (categoryId) {
        fetch('/diagnosis/equipments?category_id=' + categoryId)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                // ล้าง Dropdown ของ Survey
                surveySelect.innerHTML = '-- เลือกปัญหาความผิดปกติ --';

                if (data.length === 0) {
                    // กรณีไม่มีอุปกรณ์ในหมวดหมู่
                    equipmentSelect.innerHTML = 'ไม่มีอุปกรณ์ในหมวดหมู่นี้';
                } else {
                    // กรณีมีอุปกรณ์
                    equipmentSelect.innerHTML = '-- เลือก Equipment --';
                    data.forEach(equipment => {
                        equipmentSelect.innerHTML += `${equipment.name}`;
                    });
                }
            })
            .catch(error => {
                console.error('Error:', error);
                equipmentSelect.innerHTML = 'เกิดข้อผิดพลาดในการโหลดข้อมูล';
            });
    } else {
        // รีเซ็ต Dropdown เมื่อไม่มีการเลือก Category
        equipmentSelect.innerHTML = '-- เลือก Equipment --';
        surveySelect.innerHTML = '-- เลือกปัญหาความผิดปกติ --';
    }
}

function loadSurveys() {
    let equipmentId = document.getElementById('equipment').value;
    let surveySelect = document.getElementById('survey');

    if (equipmentId) {
        fetch('/diagnosis/surveys?equipment_id=' + equipmentId)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                if (data.length === 0) {
                    // กรณีไม่มี Survey
                    surveySelect.innerHTML = 'ไม่มีแบบสอบถามสำหรับอุปกรณ์นี้';
                } else {
                    // กรณีมี Survey
                    surveySelect.innerHTML = '-- เลือกปัญหาความผิดปกติ --';
                    data.forEach(survey => {
                        surveySelect.innerHTML += `${survey.title}`;
                    });
                }
            })
            .catch(error => {
                console.error('Error:', error);
                surveySelect.innerHTML = 'เกิดข้อผิดพลาดในการโหลดข้อมูล';
            });
    } else {
        // รีเซ็ต Dropdown เมื่อไม่มีการเลือก Equipment
        surveySelect.innerHTML = '-- เลือกปัญหาความผิดปกติ --';
    }
}

function startSurvey() {
    let surveyId = document.getElementById('survey').value; // ใช้ Survey ID
    if (surveyId) {
        // เรียก URL Custom Controller
        window.location.href = `/diagnosis/survey/start/${surveyId}`;
    } else {
        alert('กรุณาเลือก ปัญหาความผิดปกติ ก่อน');
    }
}
